mapper.select:
select 

from point_repayment
where settlement_no = #{0} 
and merchant_code = #{1}

mapper.insert:
insert into point_repayment (id, merchant_code, settlement_no, 
  request_time, settle_points, settle_amount, 
  create_time, update_time, finish_time, 
  status, err_code, err_msg
  )
values (#{id,jdbcType=BIGINT}, #{merchantCode,jdbcType=VARCHAR}, #{settlementNo,jdbcType=VARCHAR}, 
  #{requestTime,jdbcType=TIMESTAMP}, #{settlePoints,jdbcType=INTEGER}, #{settleAmount,jdbcType=DECIMAL}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{finishTime,jdbcType=TIMESTAMP}, 
  #{status,jdbcType=TINYINT}, #{errCode,jdbcType=VARCHAR}, #{errMsg,jdbcType=VARCHAR}
  )
